mapper.select:
select * from article
where title = #{title}
and body = #{body}
and boardId = #{boardId}

select * from article

	where boardId = #{boardId}


	and title like concat('%',#{searchKeyword},'%')

order by id desc

	limit #{limitStart}, #{limitTake}

select count(*) from article
where boardId = #{boardId}
and title like concat('%',#{searchKeyword},'%')
